    // transfer button
    private void jButton7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton7ActionPerformed

        create.Enter_the_accountno_text1=Entertheaccountno7.getText();
        create.Enter_the_accountno_text2=Entertheaccountno8.getText();
        create.The_balance_to_be_sent_text=Thebalancetobesent.getText();

        create.Enter_the_accountno_num1 =Integer.parseInt( create.Enter_the_accountno_text1);
        create.Enter_the_accountno_num2 =Integer.parseInt( create.Enter_the_accountno_text2);
        create.The_balance_to_be_sent_num =Integer.parseInt(create.The_balance_to_be_sent_text);
        error40.setText("");
        if(create.data[create.Enter_the_accountno_num1][4]=="Personal")
        {
            if( create.The_balance_to_be_sent_num>10000)
            {
                error40.setText("This personal account cannot transfer more than 10000 $");
            }
            else
            {
                create. balance6=Integer.parseInt(create.data[create.Enter_the_accountno_num1][6]);
                create.balance8=Integer.parseInt(create.data[create.Enter_the_accountno_num2][6]);

                create.Final_balance_after_transfer_1  = create.balance6-create.The_balance_to_be_sent_num;
                create.Final_balance_after_transfer_2 = create.balance8+create.The_balance_to_be_sent_num;

                create.data[create.Enter_the_accountno_num1][6]= Integer.toString(create.Final_balance_after_transfer_1);

                create.data[create.Enter_the_accountno_num2][6]= Integer.toString(create.Final_balance_after_transfer_2);

                value_c1.setText(create.data[create.Enter_the_accountno_num1][6]);
                value_c2.setText(create.data[create.Enter_the_accountno_num2][6]);
            }
        }

        else if(create.data[create.Enter_the_accountno_num1][4]=="Vip")
        {
            if( create.The_balance_to_be_sent_num>60000)
            {
                error40.setText("This VIP account cannot transfer more than 60000 $");
            }
            else
            {
                create. balance6=Integer.parseInt(create.data[create.Enter_the_accountno_num1][6]);
                create.balance8=Integer.parseInt(create.data[create.Enter_the_accountno_num2][6]);

                create.Final_balance_after_transfer_1  = create.balance6-create.The_balance_to_be_sent_num;
                create.Final_balance_after_transfer_2 = create.balance8+create.The_balance_to_be_sent_num;

                create.data[create.Enter_the_accountno_num1][6]= Integer.toString(create.Final_balance_after_transfer_1);

                create.data[create.Enter_the_accountno_num2][6]= Integer.toString(create.Final_balance_after_transfer_2);

                value_c1.setText(create.data[create.Enter_the_accountno_num1][6]);
                value_c2.setText(create.data[create.Enter_the_accountno_num2][6]);
            }
        }
    }//GEN-LAST:event_jButton7ActionPerformed

    // withdrawal button 
    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed

        value_c.setText("");
        status.setText("");
        error6.setText("");
        dcurrentbalance.setText("");
        error8.setText("");

        create.acc=Entertheaccountno6.getText();
        create.acc=Entertheaccountno6.getText();
        create.Accountno_index=Integer.parseInt( Entertheaccountno6.getText());

        create.Account_number=Integer.parseInt(create.data[create.Accountno_index][5]);
        if(create.Accountno_index==create.Account_number)
        {
            if(create.data[create.Accountno_index][4]=="Personal")
            {
                create.withdrawal0=Integer.parseInt(withdrawal.getText());
                if(create.withdrawal0<=10000)
                {
                    create.balance=Integer.parseInt(create.data[create.Accountno_index][6]);
                    if(create.balance>=create.withdrawal0)
                    {
                        create.value=create.balance-create.withdrawal0;

                        create.v =  Integer.toString(create.value);
                        create.data[create.Accountno_index][6]=create.v;
                        value_c.setText(create.v);
                        status.setText("Creditor");
                    }

                    else if(create.balance<create.withdrawal0)
                    {
                        create.value=create.withdrawal0-create.balance;
                        value_c.setText("0");
                        status.setText("Debit");

                    }
                    dcurrentbalance.setText(Deposit.getText());
                }
                else if(create.withdrawal0>10000)
                {

                    error6.setText("! The maximum withdrawal limit is 10000 $");
                }
            }

            else if(create.data[create.Accountno_index][4]=="Vip")
            {
                create.withdrawal0=Integer.parseInt(withdrawal.getText());
                if(create.withdrawal0<=60000)
                {
                    create.balance=Integer.parseInt(create.data[create.Accountno_index][6]);
                    if(create.balance>=create.withdrawal0)
                    {
                        create.value=create.balance-create.withdrawal0;

                        create.v =  Integer.toString(create.value);
                        create.data[create.Accountno_index][6]=create.v;
                        value_c.setText(create.v);
                        status.setText("Creditor");
                    }

                    else if(create.balance<create.withdrawal0)
                    {
                        create.value=create.withdrawal0-create.balance;
                        value_c.setText("0");
                        status.setText("Debit");

                    }
                    dcurrentbalance.setText(Deposit.getText());
                }
                else if(create.withdrawal0>60000)
                {
                    error6.setText("! The maximum withdrawal limit is 60000 $");

                }
            }
        }
        else
        {

            error8.setText("! ");
        }
    }//GEN-LAST:event_jButton6ActionPerformed

