    // create button
    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed

        if(create.Name!="" && create.Date!=""&& create.Address!="" && create.Mobileno!=""&& create.Accountno!="" )
        {
            create.Name=name.getText();
            create.Date=date.getText();
            create.Address=address.getText();
            create.Mobileno=mobile.getText();
            create.Accountno=accountno.getText();
            create.Accountno_index=Integer.parseInt( accountno.getText());

            create.data[create.Accountno_index][0]=create.Name;
            create.data[create.Accountno_index][1]=create.Date;
            create.data[create.Accountno_index][2]=create.Address;
            create.data[create.Accountno_index][3]=create.Mobileno;
            create.data[create.Accountno_index][4]=(String) jComboBox4.getSelectedItem();
            create.data[create.Accountno_index][5]=create.Accountno;
            create.data[create.Accountno_index][6]=create.price;
            create.data[create.Accountno_index][7]=(String) jComboBox1.getSelectedItem();
            create.data[create.Accountno_index][8]=(String) jComboBox2.getSelectedItem();
        }

       

        name.setText("");
        date.setText("");
        address.setText("");
        mobile.setText("");
        accountno.setText("");
    }//GEN-LAST:event_jButton3ActionPerformed

 
